const path = require('path')

module.exports = {
  module: {
    rules: [
      {
        test: /\.(scss|sass)$/,
        loaders: ['style-loader', 'css-loader', 'sass-loader'],
        include: path.resolve(__dirname, '../src'),
      },
      {
        test: /\.html$/,
        loaders: ['html-loader'],
        include: path.resolve(__dirname, '../src'),
      },
      {
        test: /\.*$/,
        loaders: ['file-loader'],
        include: path.resolve(__dirname, '../src/assets'),
      },
      {
        test: /\.css$/,
        loaders: ['style-loader', 'css-loader'],
      },
    ],
  },
}