import React   from 'react'
import {shallow} from 'enzyme'
import {Card}  from 'components/content/Card/index'

describe('Card', () => {
  it('should render', () => {
    const tree = shallow(<Card/>)
    expect(tree).toMatchSnapshot()
  })
  it('should render class names passed as props', () => {
    const tree = shallow(<Card className={'TestClassName'}>Hello</Card>)
    expect(tree.hasClass('TestClassName')).toEqual(true)
  })
  it('should render children', () => {
    const tree = shallow(<Card><div>Child!</div></Card>)
    expect(tree.contains(<div>Child!</div>)).toEqual(true)
  })
  // it('should take dark theme')
  // it('should propagate props')
})