import React       from 'react'
import injectSheet from 'react-jss'
import {css}       from 'config/css'
import color       from 'color'

const ListItem = injectSheet({
  listItem: {
    borderBottom: `1px solid ${css.colors.alto}`,
    padding: '0.5em',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    '&:first-child': {
      paddingTop: 0,
    },
    '&:last-child': {
      paddingBottom: 0,
      borderBottom: 'none',
    },
  },
  darkTheme: {
    borderBottom: `1px solid ${color(css.colors.charade).lighten(0.5).hex()}`,
  },
})(({classes, children, theme}) => (
  <li className={`${classes.listItem} ${theme === 'dark' ? classes.darkTheme : null}`}>{children}</li>
))

export {ListItem}