import React       from 'react'
import injectSheet from 'react-jss'
import {objstr}    from 'config/objectString'
import {ListItem}  from 'components/content/List/ListItem'
import {
  card,
  cardDarkTheme,
  cardLightTheme,
}                  from 'components/content/cardStyles'

const List = injectSheet({
  list: {
    ...card,
    padding: '0.5rem 0',
  },
  light: {...cardLightTheme},
  dark: {...cardDarkTheme},
})(({classes, children, theme, ...rest}) => {
  const c = objstr({
    [classes.list]: true,
    [classes.dark]: (theme === 'dark'),
    [classes.light]: (!theme || theme === 'light'),
  })
  return (
    <ul className={c}>
      {React.Children.map(children, child => {
        if (React.isValidElement(child)) {
          return React.cloneElement(child, {theme, ...rest})
        } else {
          return child
        }
      })}
    </ul>
  )
})

export {List, ListItem}