import React    from 'react'
import {mount}  from 'enzyme'
import {Button} from 'components/forms/buttons/Button'

describe('Button', () => {
  it('should render', () => {
    const tree = mount(<Button/>)
    expect(tree).toMatchSnapshot()
  })
  it('should render class names passed as props', () => {
    const component = mount(<Button className={'TestClassName'}>Hello</Button>)
    expect(component.find('button').hasClass('TestClassName')).toEqual(true)
  })
  it('should render children', () => {
    const component = mount(<Button><div>Child!</div></Button>)
    expect(component.contains(<div>Child!</div>)).toEqual(true)
  })
  // it('should not pass loggedIn down', () => {
  //   const component = mount(<Button loggedIn={true}><div>Child!</div></Button>)
  //   const children = component.children()
  //   expect(children[0].hasAttribute('loggedIn')).toEqual(false)
  // })
})