import React, {Fragment} from 'react'
import injectSheet       from 'react-jss'
import {objstr}          from 'config/objectString'

const Checkbox = injectSheet({
  checkboxInput: {},
})((props) => {
  const {classes, className, inputRef = noop, ...rest} = props

  return (
    <Fragment>
      <input
        type="checkbox"
        ref={inputRef}
        className={objstr({
          [className]: !!className,
          [classes.checkboxInput]: true,
        })}
        {...rest}
      />
    </Fragment>
  )
})

function noop() {
  return null
}

export {Checkbox}