import {css} from 'config/css'

const styles = {
  switchInput: {
    display: 'none',
  },
  switch: {
    position: 'relative',
    display: 'inline-block',
    width: '60px',
    height: '34px',
    background: css.colors.seafarer,
    borderRadius: '34px',
    cursor: 'pointer',
  },
  toggle: {
    '&:before': {
      position: 'absolute',
      content: '\'\'',
      height: '26px',
      width: '26px',
      left: '4px',
      bottom: '4px',
      background: css.colors.white,
      transition: '.4s',
      transform: 'translateX(0)',
      borderRadius: '50%',
    },
    borderRadius: '34px',
    background: ({checked}) => (checked ? css.colors.seafarer : css.colors.granite),
    boxShadow: ({focused}) => (focused ? css.shadows.low : ''),
  },
  checked: {
    background: css.colors.granite,
    '&:before': {
      transform: 'translateX(26px)',
      background: css.colors.seafarer,
    },
  },
}

export {styles}
