import React       from 'react'
import injectSheet from 'react-jss'
import {objstr}    from 'config/objectString'

const TextInput = injectSheet({
  textInput: {},
})((props) => {
  const {classes, className, inputRef = noop, ...rest} = props
  return (<input
    type="text"
    ref={inputRef}
    className={objstr({
      [className]: !!className,
      [classes.textInput]: true,
    })}
    {...rest}
  />)
})

function noop() {
  return null
}

export {TextInput}