import React       from 'react'
import injectSheet from 'react-jss'
import {css}       from 'config/css'

const styles = {
  root: {
    background: css.colors.charade,
    padding: '0.25em 0.5em',
    borderRadius: '2px',
    display: 'block',
    fontSize: '0.75em',
    letterSpacing: '0.15em',
    color: css.colors.white,
    whiteSpace: 'pre',
    maxHeight: '200px',
    overflowY: 'auto',
  },
}

const CodeComponent = ({classes, children}) => {
  return (!children) ? null : <code className={classes.root}>{children}</code>
}

const Code = injectSheet(styles)(CodeComponent)

export {Code}