import React        from 'react'
import injectSheet  from 'react-jss'
import StoryRouter  from 'storybook-react-router'
import {storiesOf}  from '@storybook/react'
import {checkA11y}  from '@storybook/addon-a11y'
import {Card}       from 'components/content/Card'
import {CardBody}   from 'components/content/Card/CardBody'
import {CardFooter} from 'components/content/Card/CardFooter'
import {Button}     from 'components/forms/buttons'
import {css}        from 'config/css'
import withTests    from 'config/withTests'
import 'stylesheets/index.sass'

storiesOf('Content/Card', module)
  .addDecorator(StoryRouter())
  .addDecorator(withTests('Card'))
  .addDecorator(checkA11y)
  .add('Default', () => (
    <Card>
      Hello!, I'm a content card
    </Card>
  ))
  .add('Dark', () => (
    <Card theme={'dark'}>
      Hello!, I'm a content card
    </Card>
  ))
  .add('Sample Card', () => (
    <SampleCard/>
  ))

const SampleCard = injectSheet({
  sampleCard: {
    width: '200px',
  },
  title: {
    margin: '0.5rem 0 0.1rem',
    fontWeight: 600,
  },
  subtitle: {
    fontSize: '0.75em',
    lineHeight: 1,
    color: css.colors.regentgrey,
  },
})(({classes}) => (
  <Card className={classes.sampleCard}>
    <CardBody>
      <img src="https://loremflickr.com/300/300"/>
      <p className={classes.title}>Wonderful Shoes</p>
      <p className={classes.subtitle}>Get some wonderful shoes</p>
    </CardBody>
    <CardFooter>
      <Button>Go to store</Button>
    </CardFooter>
  </Card>
))