import React         from 'react'
import StoryRouter   from 'storybook-react-router'
import {storiesOf}   from '@storybook/react'
import {action}      from '@storybook/addon-actions'
import {checkA11y}   from '@storybook/addon-a11y'
import {boolean}     from '@storybook/addon-knobs/react'
import {SelectField} from 'components/forms/inputs/SelectInput'
import {RadioInput}  from 'components/forms/inputs/RadioInput'
import withTests     from 'config/withTests'
import 'stylesheets/index.sass'

storiesOf('Forms/Inputs/Radio Button', module)
  .addDecorator(checkA11y)
  .addDecorator(withTests('RadioInput'))
  .addDecorator(StoryRouter())
  .add('Default', () => (
    <RadioInput
      name={'radio_input'}
      onInput={e => action()('Radio input', e.target.value, e.target, e)}
      active={boolean('Active', false)}
      checked={boolean('Checked', false)}
    />
  ))
  .add('Complete', () => (
    <RadioInput
      name={'radio_input'}
      onInput={e => action()('Radio input', e.target.value, e.target, e)}
      active={boolean('Active', false)}
      checked={boolean('Checked', false)}
      complete={true}
    />
  ))