import React         from 'react'
import StoryRouter   from 'storybook-react-router'
import {storiesOf}   from '@storybook/react'
import {action}      from '@storybook/addon-actions'
import {checkA11y}   from '@storybook/addon-a11y'
import 'stylesheets/index.sass'
import {SwitchInput} from 'components/forms/inputs/SwitchInput'
import withTests     from 'config/withTests'
import {withKnobs, boolean}             from '@storybook/addon-knobs'

storiesOf('Forms/Inputs/Switch', module)
  .addDecorator(StoryRouter())
  .addDecorator(checkA11y)
  .addDecorator(withKnobs)
  .addDecorator(withTests('SwitchInput'))
  .add('Default', () => (
    <SwitchInput
      ref={action('Set Text Input Ref')}
      className={'some classes'}
      label={'Some Checkbox'}
      name={'agree'}
      value={boolean('True/False')}
      onInput={e => {
        action('Change value')(e.target.value)
        action('Change checked')(e.target.checked)
      }}
      aria-label={'Text Input'}
    />
  ))
  .add('Default True', () => (
    <SwitchInput
      ref={action('Set Text Input Ref')}
      className={'some classes'}
      label={'Some Checkbox'}
      name={'agree'}
      value={true}
      onInput={e => {
        action('Change value')(e.target.value)
        action('Change checked')(e.target.checked)
      }}
      aria-label={'Text Input'}
      // defaultChecked={true}
    />
  ))
