import React       from 'react'
import {storiesOf} from '@storybook/react'
import {checkA11y} from '@storybook/addon-a11y'
import banner      from 'templates/banner/index.html'
import lightBanner from 'templates/banner/light.html'
import redBanner   from 'templates/banner/red.html'
import blueBanner  from 'templates/banner/blue.html'
import 'templates/banner/banner.sass'

storiesOf('Templates/Banner', module)
  .addDecorator(checkA11y)
  .addWithStaticMarkup('Default', () => (
    <div dangerouslySetInnerHTML={{__html: banner}}/>
  ))
  .addWithStaticMarkup('Light', () => (
    <div dangerouslySetInnerHTML={{__html: lightBanner}}/>
  ))
  .addWithStaticMarkup('Red', () => (
    <div dangerouslySetInnerHTML={{__html: redBanner}}/>
  ))
  .addWithStaticMarkup('Blue', () => (
    <div dangerouslySetInnerHTML={{__html: blueBanner}}/>
  ))

