import React                from 'react'
import StoryRouter          from 'storybook-react-router'
import {storiesOf}          from '@storybook/react'
import {action}             from '@storybook/addon-actions'
import {withKnobs, boolean} from '@storybook/addon-knobs/react'
import {checkA11y}          from '@storybook/addon-a11y'
import {Modal}              from 'components/widgets/Modal'
import 'stylesheets/index.sass'

storiesOf('Widgets/Modals', module)
  .addDecorator(StoryRouter())
  .addDecorator(withKnobs)
  .addDecorator(checkA11y)
  .add('Default Type, Light Theme', () => (
    <Modal open={boolean('Open', true)} title={'Modal Title'} closeHandler={action('Close Modal')}>
      <div>Hello!, I'm a content card</div>
    </Modal>
  ))
  .add('Default Type, Dark Theme', () => (
    <Modal open={true} title={'Modal Title'} closeHandler={action('Close Modal')} theme={'dark'}>
      <div>Hello!, I'm a content card</div>
    </Modal>
  ))
  .add('Error Type, Light Theme', () => (
    <Modal open={true} title={'Modal Title'} closeHandler={action('Close Modal')} type={'error'}>
      <div>Hello!, I'm a content card</div>
    </Modal>
  ))
  .add('Error Type, Dark Theme', () => (
    <Modal open={true} title={'Modal Title'} closeHandler={action('Close Modal')} theme={'dark'} type={'error'}>
      <div>Hello!, I'm a content card</div>
    </Modal>
  ))
