const log = (...params) => {
  if (process.env.REACT_APP_DEBUG === 'true') {
    console.log('%cdebug:', 'color: #2ecc71', params)
  }
}

const info = (...params) => {
  if (process.env.REACT_APP_DEBUG === 'true') {
    console.log('%cinfo:', 'color: #3498db', params)
  }
}

const todo = (...params) => {
  if (process.env.REACT_APP_DEBUG === 'true') {
    console.log('%ctodo:', 'color: #f1c40f', params)
  }
}

const error = (...params) => {
  if (process.env.REACT_APP_DEBUG === 'true') {
    console.log('%cerror:', 'color: #e74c3c', params)
  }
}

export {todo, log, info, error}
